using BurcApp.Helper;
using BurcApp.Modals;
using SQLite;
using Xamarin.Forms;

namespace BurcApp.Tools.Database
{
    public class SQLiteManagerDB
    {
        public SQLiteConnection sqliteConnectionDB;
        static bool kayit = false;

        public SQLiteManagerDB()
        {
            sqliteConnectionDB = DependencyService.Get<ISQLiteConnection>().GetConnection(App.DBName);
            if (Device.RuntimePlatform == Device.iOS)
            {
                if (!SQLiteManagerDB.kayit)
                {
                    TablolarıOlustur();
                    BurclarTablosuDoldur();
                    YukselenBurcTablosuDoldur();
                    BurcUyumuTablosuDoldur();
                    SQLiteManagerDB.kayit = true;
                }
            }
        }

        public void TablolarıOlustur()
        {
            sqliteConnectionDB.DropTable<Burclar>();
            sqliteConnectionDB.DropTable<YukselenBurc>();
            sqliteConnectionDB.DropTable<BurcUyumu>();

            sqliteConnectionDB.CreateTable<GecisReklamiTiklama>();
            sqliteConnectionDB.CreateTable<Burclar>();
            sqliteConnectionDB.CreateTable<YukselenBurc>();
            sqliteConnectionDB.CreateTable<BurcUyumu>();

            if(sqliteConnectionDB.Table<GecisReklamiTiklama>().Count() <= 0)
            {
                GecisReklamiTiklama gecisReklamiTiklama = new GecisReklamiTiklama();
                gecisReklamiTiklama.TiklanmaSayisi = 0;
                sqliteConnectionDB.Insert(gecisReklamiTiklama);
            }
        }

